import React, { useState } from "react";
import { useMutation, gql} from "@apollo/client";
import 'semantic-ui-css/semantic.min.css'
import { Button, Form, Grid, Header, Segment } from 'semantic-ui-react'
import { useHistory } from 'react-router-dom';
import {v4 as uuidv4} from 'uuid';

export const CREATE_USER_MUTATION = gql`
mutation Mutation($userLoginEMail: String!) {
  userLogin(eMail: $userLoginEMail) {
    id
    email
    token
  }
}
`;

function LoginForm() {

  const history = useHistory();
  
  const [login, { loading, error }] = useMutation(CREATE_USER_MUTATION);

  if (loading) return 'Submitting...';
  if (error) return `Submission error! ${error.message}`;
  console.log(data,loading,error);

  return (
    <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
    <Grid.Column style={{ maxWidth: 450 }}>
      <Header as='h2' color='red' textAlign='center'>
        Login
      </Header>

      <Form size='large' fluid icon='user' iconPosition='left'
      >
          <Button color='red' fluid size='large' type="submit">
            Login
          </Button>
      </Form>
    </Grid.Column>
  </Grid>
  );

}
export default LoginForm;
