import React, { useState } from "react";
import { useMutation, gql} from "@apollo/client";
import 'semantic-ui-css/semantic.min.css'
import { Button, Form, Grid, Header, Segment } from 'semantic-ui-react'
import { useHistory } from 'react-router-dom';
import {v4 as uuidv4} from 'uuid';

const CREATE_USER_MUTATION = gql`
mutation Mutation($userLoginEMail: String!) {
  userLogin(eMail: $userLoginEMail) {
    id
    email
  }
}
`;

function LoginForm() {

  const history = useHistory();
  let input;
  const [addUser, { data, loading, error }] = useMutation(CREATE_USER_MUTATION);

  if (loading) return 'Submitting...';
  if (error) return `Submission error! ${error.message}`;

  
    history.push("/todos");
  
  
  return (
    <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
    <Grid.Column style={{ maxWidth: 450 }}>
      <Header as='h2' color='red' textAlign='center'>
        Login
      </Header>
      <Form size='large'>
        <Segment>
          <Form.Input
          fluid icon='mail' 
          iconPosition='left' 
          placeholder='Email'
          onSubmit={e => {
            e.preventDefault();
            addUser({ variables: {email: input.value}});
            input.value = '';
          }}
          />
          <Button color='red' fluid size='large' type="submit">
            Login
          </Button> 
        </Segment>
        </Form>
    </Grid.Column>
  </Grid>
  );

}
export default LoginForm;
